import jxl.*;
import jxl.write.*
import java.io.*
import java.io.file.*
import java.util.*
import java.lang.*
import jxl.read.biff.BiffException;
import net.sf.*
import net.sf.json.*
import net.sf.json.groovy.*
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import groovy.json.JsonBuilder
import com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus
import com.eviware.soapui.impl.wsdl.teststeps.RestTestRequestStep

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectDir = groovyUtils.projectPath

def inputfile =  projectDir + "/DetermineReturnReasonsAllowed/DetermineReturnReasonsAllowed_Input.xls"
def outputfile = projectDir + "/DetermineReturnReasonsAllowed/DetermineReturnReasonsAllowed_Output.xls"


//Creating workbook for reading value from Excel Sheet

Workbook wb = Workbook.getWorkbook(new File(inputfile))
Sheet sh = wb.getSheet("LineItem")
Sheet jsonInput = wb.getSheet("JSON")

//reading data from input Excel sheet
rc = sh.getRows();
cc = sh.getColumns();
Cell jreq = jsonInput.getCell(0,0)

//Creating Workbook for writing value in Excel Sheet
WritableWorkbook wb1= Workbook.createWorkbook(new File(outputfile));
WritableSheet sheet1 = wb1.createSheet("ActualResults",0);

//Header declaration for output file
addCell(0,0,"lineItems",sheet1);
addCell(0,1,"lineItemID",sheet1);
addCell(1,1,"productCategory",sheet1);
addCell(2,1,"productSubCategory1",sheet1);
addCell(3,1,"productSubCategory2",sheet1);
addCell(4,1,"productSubCategory3",sheet1);
addCell(5,1,"productSubCategory4",sheet1);
addCell(6,1,"returnReasonRestricted",sheet1);

addCell(7,1,"lineItemID",sheet1);
addCell(8,1,"productCategory",sheet1);
addCell(9,1,"productSubCategory1",sheet1);
addCell(10,1,"productSubCategory2",sheet1);
addCell(11,1,"productSubCategory3",sheet1);
addCell(12,1,"productSubCategory4",sheet1);
addCell(13,1,"returnReasonRestricted",sheet1);

addCell(14,0,"PASS/FAIL",sheet1);
addCell(15,0,"FAILED DUE TO",sheet1);

//new testStepName
def newTestStep=null

try {  
               def req = jreq.getContents()
               //Assigning request value dynamically
               //request
               def slurper = new JsonSlurper().parseText(req);
               def requestTemplet = new JsonBuilder(slurper);

               for (i=2;i<rc;i++){
                           // Input for TestCaseName
                         Cell varTestCaseName = sh.getCell(0,i)
                        def TestCaseName = varTestCaseName.getContents()

                                //Input for LineItem 1                                                                                            
                                requestTemplet.content.lineItems[0].lineItemID = sh.getCell(1,i).getContents().toString()                                 
                                requestTemplet.content.lineItems[0].productCategory = sh.getCell(2,i).getContents().toString()                                
                                requestTemplet.content.lineItems[0].productSubCategory1 = sh.getCell(3,i).getContents().toString()
                                requestTemplet.content.lineItems[0].productSubCategory2 = sh.getCell(4,i).getContents().toString()
                                requestTemplet.content.lineItems[0].productSubCategory3 = sh.getCell(5,i).getContents().toString()                                
                                requestTemplet.content.lineItems[0].productSubCategory4 = sh.getCell(6,i).getContents().toString()                               

                                 //Input for LineItem 2                           
                               requestTemplet.content.lineItems[1].lineItemID = sh.getCell(7,i).getContents().toString()
                                requestTemplet.content.lineItems[1].productCategory = sh.getCell(8,i).getContents().toString()
                                requestTemplet.content.lineItems[1].productSubCategory1 = sh.getCell(9,i).getContents().toString()
                                requestTemplet.content.lineItems[1].productSubCategory2 = sh.getCell(10,i).getContents().toString()
                                requestTemplet.content.lineItems[1].productSubCategory3 = sh.getCell(11,i).getContents().toString()                                
                                requestTemplet.content.lineItems[1].productSubCategory4 = sh.getCell(12,i).getContents().toString()

	                             //Assign json request to variable
                       def jsonReqAsString = requestTemplet.toPrettyString()
                       def restRequest = testRunner.testCase.getTestStepByName('JSON Request');
                       restRequest.setPropertyValue('Request',jsonReqAsString);

                                //teststep cloned: Start
                        def getTestCase = testRunner.testCase;
                        def testst = testRunner.testCase.getTestStepByName("JSON Request");                 
                            newTestStep = TestCaseName
                            cleanUp(newTestStep)
                            getTestCase.cloneStep(restRequest,newTestStep)                             
                         //teststep cloned: End

                         //Executing the testcase
                        testRunner.runTestStepByName(newTestStep)

                         //retrive response         
                       def ResponseMessage = testRunner.testCase.testSteps[newTestStep].testRequest.response.contentAsString

                         //new JsonSlurper to parse and get the response in Json format
                        def slurper1 = new JsonSlurper()
                        def json = slurper1.parseText(ResponseMessage)                    

                          // get response in excel                         		                        
                          //check if the array LineItems is not empty
                          if (json.lineItems!=null)
                          {
                          //initialize the cel postion for lineItems array
                          int cellineItems=0;                                                     
                          //Get the array size for lineItems
                        for(int arrlineItems=0; arrlineItems<json.lineItems.size(); arrlineItems++)
                        {                        		                      		
                        		addCell(cellineItems,i,json.lineItems[arrlineItems].lineItemID.toString(),sheet1); 
                        		cellineItems=cellineItems+1;

                        		addCell(cellineItems,i,json.lineItems[arrlineItems].productCategory.toString(),sheet1); 
                        		cellineItems=cellineItems+1;

                        		addCell(cellineItems,i,json.lineItems[arrlineItems].productSubCategory1.toString(),sheet1); 
                        		cellineItems=cellineItems+1;

                        		addCell(cellineItems,i,json.lineItems[arrlineItems].productSubCategory2.toString(),sheet1); 
                        		cellineItems=cellineItems+1;

                        		addCell(cellineItems,i,json.lineItems[arrlineItems].productSubCategory3.toString(),sheet1); 
                        		cellineItems=cellineItems+1;

                        		addCell(cellineItems,i,json.lineItems[arrlineItems].productSubCategory4.toString(),sheet1); 
                        		cellineItems=cellineItems+1;

                        		addCell(cellineItems,i,json.lineItems[arrlineItems].returnReasonRestricted.toString(),sheet1); 
                        		cellineItems=cellineItems+1;
                        }                                            
               }                   

                      //dynamic asserstion :Start
                   def testStepForAssertion = testRunner.testCase.getTestStepByName(newTestStep);
                                //ValidHTTPStatusCodes 200 Assertion
                  addAssertionValidHTTPStatusCodes(testStepForAssertion,"Valid HTTP Status Codes_"+i)

                             //get the expected values                              
						//check the existence of lineItems
            		if(json.lineItems.toString()!= "[]")
            		{       
            			 //initialize the cel postion for LineItem array for assertion                      
                                int AssLineItem=13;

	            			for(int arrforlineItems=0; arrforlineItems<json.lineItems.size(); arrforlineItems++)
	                        {                       	         

		                        String lineItemID = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,lineItemID,"lineItems["+arrforlineItems+"].lineItemID","lineItemID")
		                        AssLineItem=AssLineItem+1;

		                        String productCategory = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,productCategory,"lineItems["+arrforlineItems+"].productCategory","productCategory")
		                        AssLineItem=AssLineItem+1;

		                        String productSubCategory1 = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,productSubCategory1,"lineItems["+arrforlineItems+"].productSubCategory1","productSubCategory1")
		                        AssLineItem=AssLineItem+1;

		                        String productSubCategory2 = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,productSubCategory2,"lineItems["+arrforlineItems+"].productSubCategory2","productSubCategory2")
		                        AssLineItem=AssLineItem+1;

		                        String productSubCategory3 = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,productSubCategory3,"lineItems["+arrforlineItems+"].productSubCategory3","productSubCategory3")
		                        AssLineItem=AssLineItem+1;

		                        String productSubCategory4 = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,productSubCategory4,"lineItems["+arrforlineItems+"].productSubCategory4","productSubCategory4")
		                        AssLineItem=AssLineItem+1;

		                        String returnReasonRestricted = sh.getCell(AssLineItem,i).getContents().toString()
		                        addAssertionJsonPathMatch(testStepForAssertion,returnReasonRestricted,"lineItems["+arrforlineItems+"].returnReasonRestricted","returnReasonRestricted")
		                        AssLineItem=AssLineItem+1;                     
	                        }	                       
            		}


                                //dynamic asserstion :End
                            assertionResult = testStepForAssertion.getAssertionStatus()
             if(assertionResult.toString().equals("FAILED")){

                                       addCell(14,i,"Fail",sheet1);
                                   for( assertion in testStepForAssertion.assertionList ){
                                  for( e in assertion.errors ){

                                                     addCell(15,i,e.message,sheet1);
                                                }
                                   }

                                }

                                else{
                                   addCell(14,i,"PASS",sheet1);
                                }
       }

      wb1.write();
            log.info "TestCase Execution Completed successfull"

}

catch(Exception e)
{

       log.info(e)                         
}

finally

{
    //close workbook
     wb.close();
     wb1.close();     

}

//Method code
def addAssertionJsonPathMatch(RestTestRequestStep testStepForAssertion,String expectedContent,String xPath,String assertionName) {
          def assertionType = testStepForAssertion.addAssertion("JsonPath Match")
                                assertionType.name = assertionName
                                assertionType.setPath(xPath)
                                assertionType.setExpectedContent(expectedContent)
                                assertionType.setAllowWildcards(true) 

}

//Method code
def addAssertionValidHTTPStatusCodes(RestTestRequestStep testStepForAssertion,String assertionName) {

         def StatusCodeAssertionxpath = testStepForAssertion.addAssertion("Valid HTTP Status Codes")
             StatusCodeAssertionxpath.name = assertionName
             StatusCodeAssertionxpath.codes=200

}

//method to delete
def cleanUp(String requestName){

                //remove the teststep::Start
                def testCase = testRunner.testCase
                def testStep = testCase.getTestStepByName(requestName)

                if (testStep!=null){


                                    testCase.removeTestStep(testStep)
                }

            //remove the teststep::End

} 

//method to add Cell to sheet1

def addCell(int colmNum,int rowNum,String lblName,WritableSheet sheet1 ){

                 Label header = new Label(colmNum,rowNum,lblName);
                sheet1.addCell(header);               

}

