                        celNumForpriorAuthorizations = celNumForpriorAuthorizations+1; 
                 }//close for For loop
             }//close if.



                      def testStepForAssertion = testRunner.testCase.getTestStepByName(newTestStep);
                       addAssertionValidHTTPStatusCodes(testStepForAssertion,"Valid HTTP Status Codes_"+i)

                    if(json.priorAuthorizations.size()>=1) { 
                    	  int celForpriorAuthorizations=76;
                   		for(int arrForpriorAuthorization=0; arrForpriorAuthorization<json.priorAuthorizations.size(); arrForpriorAuthorization++){


                          lineItemID = sh.getCell(celForpriorAuthorizations,i).getContents()
                       addAssertionJsonPathMatch(testStepForAssertion,lineItemID,"priorAuthorizations["+arrForpriorAuthorization+"].lineItemID","lineItemID")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                          planID = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,planID,"priorAuthorizations["+arrForpriorAuthorization+"].planID","planID")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                         orderId = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,orderId,"priorAuthorizations["+arrForpriorAuthorization+"].orderId","orderId")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        prescriptionNeededForPriorAuth = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,prescriptionNeededForPriorAuth,"priorAuthorizations["+arrForpriorAuthorization+"].prescriptionNeededForPriorAuth","prescriptionNeededForPriorAuth")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       cnNeededForPriorAuth = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,cnNeededForPriorAuth,"priorAuthorizations["+arrForpriorAuthorization+"].cnNeededForPriorAuth","cnNeededForPriorAuth")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       lbNeededForPriorAuth = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,lbNeededForPriorAuth,"priorAuthorizations["+arrForpriorAuthorization+"].lbNeededForPriorAuth","lbNeededForPriorAuth")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        requiredForBilling = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,requiredForBilling,"priorAuthorizations["+arrForpriorAuthorization+"].requiredForBilling","requiredForBilling")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        requiredForShipping = sh.getCell(celForpriorAuthorizations,i).getContents()
                       addAssertionJsonPathMatch(testStepForAssertion,requiredForShipping,"priorAuthorizations["+arrForpriorAuthorization+"].requiredForShipping","requiredForShipping")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                          submissionPointOfContact = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,submissionPointOfContact,"priorAuthorizations["+arrForpriorAuthorization+"].submissionPointOfContact","submissionPointOfContact")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        phone = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,phone,"priorAuthorizations["+arrForpriorAuthorization+"].phone","phone")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        fax = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,fax,"priorAuthorizations["+arrForpriorAuthorization+"].fax","fax")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        payerRequirementURL = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,payerRequirementURL,"priorAuthorizations["+arrForpriorAuthorization+"].payerRequirementURL","payerRequirementURL")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

