                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       turnAroundTimeInDays = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,turnAroundTimeInDays,"priorAuthorizations["+arrForpriorAuthorization+"].turnAroundTimeInDays","turnAroundTimeInDays")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       timeToFollowUpInDays = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,timeToFollowUpInDays,"priorAuthorizations["+arrForpriorAuthorization+"].timeToFollowUpInDays","timeToFollowUpInDays")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                        followUpMethod = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,followUpMethod,"priorAuthorizations["+arrForpriorAuthorization+"].followUpMethod","followUpMethod")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       contactMethodCode = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,contactMethodCode,"priorAuthorizations["+arrForpriorAuthorization+"].submissionContactMethod.contactMethodCode","contactMethodCode")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       followUpPointOfContact = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,followUpPointOfContact,"priorAuthorizations["+arrForpriorAuthorization+"].followUpPointOfContact","followUpPointOfContact")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       priorAuthorizationRequired = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,priorAuthorizationRequired,"priorAuthorizations["+arrForpriorAuthorization+"].priorAuthorizationRequired","priorAuthorizationRequired")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                       message = sh.getCell(celForpriorAuthorizations,i).getContents().toString()
                       addAssertionJsonPathMatch(testStepForAssertion,message,"priorAuthorizations["+arrForpriorAuthorization+"].message","message")
                       celForpriorAuthorizations = celForpriorAuthorizations+1

                  	}
                 }


                         assertionResult = testStepForAssertion.getAssertionStatus()
             if(assertionResult.toString().equals("FAILED")){

                                       addCell(127,i,"Fail",sheet1);
                                   for( assertion in testStepForAssertion.assertionList ){
                                  for( e in assertion.errors ){

                                                     addCell(128,i,e.message,sheet1);

                                                } 

                                   }

                                }


                                else{

                                   addCell(127,i,"PASS",sheet1);

                                }


       }

      wb1.write();
            log.info "TestCase Execution Completed successfull"



                }             

catch(Exception e)
{

                 log.info(e)                         
}

finally
{


     wb.close();
     wb1.close();     

}

def addAssertionJsonPathMatch(RestTestRequestStep testStepForAssertion,String expectedContent,String xPath,String assertionName) {
          def assertionType = testStepForAssertion.addAssertion("JsonPath Match")
                                assertionType.name = assertionName
                                assertionType.setPath(xPath)
                                assertionType.setExpectedContent(expectedContent)
                                assertionType.setAllowWildcards(true) 

}

def addAssertionValidHTTPStatusCodes(RestTestRequestStep testStepForAssertion,String assertionName) {

         def StatusCodeAssertionxpath = testStepForAssertion.addAssertion("Valid HTTP Status Codes")
             StatusCodeAssertionxpath.name = assertionName
             StatusCodeAssertionxpath.codes=200

}

def cleanUp(String requestName){

                def testCase = testRunner.testCase
                def testStep = testCase.getTestStepByName(requestName)

                if (testStep!=null){


                                    testCase.removeTestStep(testStep)
                }

    }

def addCell(int colmNum,int rowNum,String lblName,WritableSheet sheet1 ){

                 Label header = new Label(colmNum,rowNum,lblName);
                sheet1.addCell(header);               
}

