                 }//close for For loop
             }//close if.



                      def testStepForAssertion = testRunner.testCase.getTestStepByName(newTestStep);
                       addAssertionValidHTTPStatusCodes(testStepForAssertion,"Valid HTTP Status Codes_"+i)

                    if(json.priorAuthorizations.size()>=1) { 
                    	  int celForpriorAuthorizations=38;
                   		for(int arrForpriorAuthorization=0; arrForpriorAuthorization<json.priorAuthorizations.size(); arrForpriorAuthorization++){


