                                                     addCell(4,i,e.message,sheet1);

                                                } 

                                   }

                                }


                                else{

                                   addCell(3,i,"PASS",sheet1);

                                }


       }

      wb1.write();
            log.info "TestCase Execution Completed successfull"



                }             

catch(Exception e)
{

                 log.info(e)                         
}

